use std::collections::HashSet;

fn main(){
    let text = "";

    for start_pos in 0..text.len()-14{
        let mut four = HashSet::<char>::new();
        for exact_pos in start_pos..start_pos+14{
            let first_len = four.len();
            four.insert(text.chars().nth(exact_pos).unwrap());
            let after_len = four.len();
            if first_len == after_len{
                break;
            }
        }
        if four.len() == 14{
            println!("{}", start_pos+14);
            break;
        }
    }
}